
// Minimal bootstrap-like JS for modal show/hide and navbar toggler
document.addEventListener('click', function(e){
  // Navbar toggler: toggle .show on next sibling collapse (simple)
  if(e.target.matches('.navbar-toggler')){
    var target = document.getElementById(e.target.getAttribute('data-bs-target').replace('#','')) || document.querySelector(e.target.getAttribute('data-bs-target'));
    if(target) target.classList.toggle('show');
  }
  // Modal open buttons
  if(e.target.matches('[data-bs-toggle="modal"]')){
    var sel = e.target.getAttribute('data-bs-target');
    var modal = document.querySelector(sel);
    if(modal) modal.classList.add('show');
  }
  // Modal close
  if(e.target.matches('[data-bs-dismiss="modal"]') || e.target.matches('.modal')){
    var modal = e.target.closest('.modal') || document.querySelector('.modal.show');
    if(modal) modal.classList.remove('show');
  }
}, false);
